INSERT INTO `book` VALUES (36, 'HTML', ' HTML CSS JavaScript基础教程 Web前端开发精品课', '莫振杰', '人民邮电出版社', '初学优选 入门经典 针对前端新手全新打造，一本书搞定 HTML、CSS、JavaScript 精品呈现 通俗易懂 语言轻松幽默，讲解一针见血，从全新角度讲透前端开发核心技术 五位一体 全面服务 在线教程 在线问答 面试练习 源码素材 课件 PPT （获取方式请加本书封底QQ群）', 'http://img3m7.ddimg.cn/44/5/25160597-1_b_3.jpg', 'http://product.dangdang.com/25160597.html');
