INSERT INTO `book` VALUES (70, 'C#', ' C Primer Plus 第6版 中文版 C语言入门经典教程', '[美]史蒂芬・普拉达（Stephen Prata）', '人民邮电出版社', '经久不衰的C语言畅销经典教程针对C11标准进行全面更新《C Primer Plus（第6版）中文版》是一本经过仔细测试、精心设计的完整C语言教程，它涵盖了C语言编程中的核心内容。《C Primer Plus（第6版）中文版》作为计算机科学的经典著作，讲解了包含结构化代码和自顶向下设计在内的程序设计原则。与以前的版本一样，作者的目标仍旧是为读者提供一本入门型、条理清晰、见解深刻的C语言教程。作者把基础的编程概念与C语言的细节很好地融合在一起，并通过大量短小精悍的示例同时演示一两个概念，通过学以致用的方式鼓励读者掌握新的主题。每章末尾的复习题和编程练习题进一步强化了重要的信息，有助于读者理解和消化那些难以理解的概念。本书采用了友好、易于使用的编排方式，不仅适合打算认真学习C语言编程的学生阅读，也适合那些精通其他编程语言，但希望', 'http://img3m2.ddimg.cn/43/13/23958142-1_b_12.jpg', 'http://product.dangdang.com/23958142.html');
INSERT INTO `book` VALUES (71, '数据安全', ' 大数据安全与隐私保护', '冯登国 等', '清华大学出版社', '本书结合作者在大数据安全与隐私保护领域的科研实践，提出大数据安全与隐私保护理论基础和技术体系框架，并对大数据安全与隐私保护面临的主要问题进行系统性阐述。本书重点介绍安全检索技术，隐私保护技术，安全存储与访问控制技术，以及安全处理技术，从技术核心贡献、领域发展综述以及1新研究进展等不同角度进行阐述，有助于感兴趣的读者较为全面地理解和把握这些技术。 本书可作为从事网络空间安全、信息安全和隐私保护研究的科研人员，网络空间安全、信息安全和密码学专业的研究生，以及相关专业的大学高年级本科生的教科书或参考资料。', 'http://img3m8.ddimg.cn/26/26/26315018-1_b_1.jpg', 'http://product.dangdang.com/26315018.html');
INSERT INTO `book` VALUES (72, '数据安全', ' 大数据安全', '丁锋', '中国言实出版社', '大数据安全概念在研究和应用领域都大行其道，大数据安全成了无法回避的现实问题，值得人们关注和重视。《大数据安全》将努力使读者对大数据安全形成一个较为完整的轮廓。', 'http://img3m4.ddimg.cn/78/10/1900651974-1_b_2.jpg', 'http://product.dangdang.com/1900651974.html');
