INSERT INTO `book` VALUES (84, 'MVC', ' Spring MVC + MyBatis快速开发与项目实战', '黄文毅', '清华大学出版社', '与同类书相比,本书的主要特色是，内容来自于一线互联网公司的工程实践，着重展现新版本Spring 5 Spring MVC 5 MyBatis 3.4.6核心技术的原理剖析与各种热点技术的整合应用与项目实践，帮助读者通过完整的项目实例了解和学习SSM框架，又好又快地掌握SSM的开发技能。', 'http://img3m9.ddimg.cn/84/12/26436549-1_b_3.jpg', 'http://product.dangdang.com/26436549.html');
