INSERT INTO `book` VALUES (137, 'GIS', ' 基于GIS的数量方法与应用（附光盘）', '（美）王法辉 著，姜世国，滕骏华 译', '商务印书馆', '本书特点 ●包括常规的空间分析任务、基本和高级的数量方法，都在GIS环境下实现 ●用各种社会科学的应用实例讲解上述方法和技能 ●强调用研究实例中的数据演示详细步骤 ●应用课题的选择都与公共政策有关 ●实例分析以ArcGIS、SAS和其他免费软件示范 ●所附cD―ROM包含所有数据和程序', 'http://img3m0.ddimg.cn/56/29/20618390-1_b_0.jpg', 'http://product.dangdang.com/20618390.html');
INSERT INTO `book` VALUES (138, 'GIS', ' GIS工程师训练营', 'SuperMap图书编委会', '清华大学出版社', '由从事多年GIS项目开发的工作者撰写，凝聚实践精华 精选十几个应用场景和案例，引导读者逐步掌握三维GIS实现方法 系统介绍三维GIS开发的要点、难点，提供实战演练，注重实操 从三维GIS数据制作、C／S应用开发N B／S应用实战，全方位解读二三维―体化思路 配套光盘提供试用版软件、完整的示例程序和数据', 'http://img3m0.ddimg.cn/72/3/23338530-1_b_1.jpg', 'http://product.dangdang.com/23338530.html');
