INSERT INTO `book` VALUES (172, '网络协议', ' 网络协议分析', '寇晓蕤，罗军勇，蔡延荣 编著', '机械工业出版社', '本书基于作者近10年的教学经验编写而成。书中以TCP／IP协议族中构建Internet所必需的、与我们交互*直观的协议作为主题，详细讨论了TCP／lP的体系结构和基本概念，并深入分析了各个协议的设计思想、流程及其所解决的问题。各个协议的应用及安全缺陷也被体现于相应章节中，以便读者对这些协议有更为深入的了解。 本书特点', 'http://img3m5.ddimg.cn/14/12/20640425-1_b_0.jpg', 'http://product.dangdang.com/20640425.html');
INSERT INTO `book` VALUES (173, '网络协议', ' 网络协议实践教程（第2版）', '陈虹、李建东、徐娇月、李婕娜、张志杰', '清华大学出版社', '本书主要讲解IEEE 802协议族和TCP/IP协议族中主要协议的相关内容。 本书提供15个相关实验，各实验均给出了实验目的、实验准备、实验原理、实验过程及思考题等。目的明确、操作性强，通过实验可以使学生较好地理解掌握抽象的网络协议知识。 本书配套PPT课件、习题答案等资源。', 'http://img3m1.ddimg.cn/39/35/24028131-1_b_6.jpg', 'http://product.dangdang.com/24028131.html');
