INSERT INTO `book` VALUES (212, '虚拟化', ' 虚拟化技术应用与实践', '陈亚威 蒋迪', '人民邮电出版社', '本书全面介绍了目前主流的各类虚拟化技术，并为每类虚拟化技术制定了一个难度适中的实践项目，能够让读者充分感受到不同虚拟化技术的特点。 本书采用项目化教学的方式组织内容，通过实践加深理解，实现知识与技能合一，有助于 教、学、做一体化 教学的实施。', 'http://img3m4.ddimg.cn/66/20/26909454-1_b_1.jpg', 'http://product.dangdang.com/26909454.html');
