INSERT INTO `book` VALUES (236, 'Go语言', ' Go语言学习笔记', '雨痕 著', '电子工业出版社', '传说中的雨痕老师终于将自己的《Go学习笔记》付梓了！ 一如既往的低调，也一如既往的务实。本书文风极简，干净利落。 上卷（基于Go 1.6）细致解析语言规范相关细节，下卷（基于Go 1.5.1）则对运行时源码做出深度剖析。', 'http://img3m6.ddimg.cn/48/16/23997846-1_b_9.jpg', 'http://product.dangdang.com/23997846.html');
INSERT INTO `book` VALUES (237, 'Go语言', ' Go语言核心编程', '李文塔', '电子工业出版社', 'Go语言精华部分 并发和小而美的类型系统。 Go陷阱 主要论述使用Go过程中大家很困惑或容易出错的地方。 Go语言的哲学 总结Go的设计哲学，懂得Go作者的设计意图，才能更好地使用该语言。', 'http://img3m3.ddimg.cn/93/29/25351023-1_b_3.jpg', 'http://product.dangdang.com/25351023.html');
