INSERT INTO `book` VALUES (264, 'HDFS', ' 高可用性的HDFS(附光盘Hadoop分布式文件系统深度实践) 正版 文艾,王磊 9787302282587', '文艾,王磊', '清华大学出版社', NULL, 'http://img3m4.ddimg.cn/92/14/1025272604-1_b_1.jpg', 'http://product.dangdang.com/1025272604.html');
INSERT INTO `book` VALUES (265, 'Hive', ' Hive实战', '[美] 斯科特・肖（Scott Shaw）、[南非] 安德烈亚斯・弗朗索瓦・弗穆', '人民邮电出版社', '面对与日俱增的海量数据，能否从中提取商业价值直接关乎公司的市场竞争力。Hive是大数据生态圈必不可少的数据仓库，它为存储在Hadoop文件系统中的数据提供便捷的类SQL查询、统计和分析，从而为公司进入大数据生态圈并迈向商业智能铺平道路。 本书着眼于Hive在真实环境中的应用，内容囊括Hive的方方面面，是针对大数据分析人员的实践参考指南。 - 理解Hive的架构和数据类型 - 高效执行DML操作 - 查询半结构化数据 - 巧妙提升Hive的性能 - 了解Hive的安全性及发展趋势', 'http://img3m9.ddimg.cn/26/19/25580339-1_b_2.jpg', 'http://product.dangdang.com/25580339.html');
INSERT INTO `book` VALUES (266, 'Hive', ' Hive编程（影印版）', '(美)卡普里奥罗 等', '东南大学出版社', NULL, 'http://img3m4.ddimg.cn/57/16/23350494-1_b_2.jpg', 'http://product.dangdang.com/23350494.html');
INSERT INTO `book` VALUES (267, 'Hive', ' Hive编程指南', '（美）Edward Capriolo Dean Wampler Jason Rutherglen 著', '人民邮电出版社', '市场中**本Hive图书。 Hive在Hadoop系统中的应用趋势比较可观。', 'http://img3m0.ddimg.cn/17/14/1900722500-1_b_1.jpg', 'http://product.dangdang.com/1900722500.html');
