INSERT INTO `book` VALUES (270, 'MapReduce', ' MapReduce 2.0源码分析与编程实战', '王晓华', '人民邮电出版社', '资深云计算技术讲师潜心力作 CSDN Hadoop版主鼎力推荐 深入浅出、结合实例，帮你在实战中掌握MapReduce 2.0编程的精髓', 'http://img3m2.ddimg.cn/83/27/1900373492-1_b_2.jpg', 'http://product.dangdang.com/1900373492.html');
INSERT INTO `book` VALUES (271, 'MapReduce', ' MapReduce设计模式', '（美）Donald Miner,Adam Shook', '信息技术第一出版分社', '到目前为止，基于MapReduce框架的设计模式散见于各种研究论文、博客和书籍中。这本书将各种有价值的MapReduce设计模式汇集在一起，形成一本独特的合集，可以帮读者节省大量的时间和精力，无论读者身处哪个领域，使用哪种编程语言，使用什么开发框架。 书中对每一种模式都会详细解释其使用的上下文、可能存在的陷阱及使用的注意事项，以帮助读者在对大数据问题架构建模时避免常见的设计错误。本书还提供了MapReduce的一个完整综述，解释其起源和实现，并说明设计模式如此重要的原因。书中的所有示例代码都是基于Hadoop平台编写的。', 'http://img3m2.ddimg.cn/42/36/1900670352-1_b_1.jpg', 'http://product.dangdang.com/1900670352.html');
INSERT INTO `book` VALUES (272, 'MapReduce', ' Hadoop MapReduce v2参考手册 第2版（影印版）', '（美）冈纳拉森 著，', '东南大学出版社', NULL, 'http://img3m0.ddimg.cn/80/14/23841260-1_b_3.jpg', 'http://product.dangdang.com/23841260.html');
INSERT INTO `book` VALUES (273, 'MapReduce', ' Hadoop MapReduce实战手册', '（斯里兰卡） Srinath Perera,Thilina Gunarathne', '人民邮电出版社', '本书是使用Hadoop生态系统处理大规模复杂数据集的一站式指南。本书先为读者介绍了简单的示例，然后深到解决深度大数据问题的用例。 本书将教会读者如何驾驭Hadoop生态系统中的各组件，包括HBase、Hadoop、Pig以及Mahout，然后让读者学会如何安装云环境来完成Hadoop MapReduce计算。本书还将用*真实的示例教会读者如何处理大规模复杂数据集。 本书将教会读者', 'http://img3m8.ddimg.cn/24/28/1900702608-1_b_1.jpg', 'http://product.dangdang.com/1900702608.html');
INSERT INTO `book` VALUES (274, 'MapReduce', ' Flume日志收集与MapReduce模式', '（美）霍夫曼（Hoffman,S.）,（美）佩雷拉（Perera,S.）', '机械工业出版社', NULL, 'http://img3m9.ddimg.cn/15/15/1900650129-1_b_2.jpg', 'http://product.dangdang.com/1900650129.html');
INSERT INTO `book` VALUES (275, 'Hadoop生态', ' Hadoop数据分析', '[美]本杰明・班福特（Benjamin Bengfort）、[美]珍妮・基姆（J', '人民邮电出版社', '通过提供分布式数据存储和并行计算框架，Hadoop已经从集群计算的抽象演变成了大数据操作系统。本书从数据科学的视角，介绍Hadoop集群计算和分析，重点关注可构建的具体分析、数据仓储技术和高阶数据流。 书中主要内容如下', 'http://img3m8.ddimg.cn/46/19/25255738-1_b_3.jpg', 'http://product.dangdang.com/25255738.html');
INSERT INTO `book` VALUES (276, 'Hadoop生态', ' Hadoop生态系统', 'Kevin Sitto', '中国电力出版社', '如果你的组织即将开始进入大数据的世界，那么可能不仅需要决定Apache Hadoop这个平台是否适合使用，还需要决定Hadoop中哪些组件*适合完成你的任务。本书将帮助你更容易地完成这项工作。本书将Hadoop的生态系统分解为一个个简略的、容易理解的小段内容，以便读者可以快速理解Hadoop项目、子项目及其相关技术是如何一起工作的。', 'http://img3m5.ddimg.cn/56/24/24060125-1_b_5.jpg', 'http://product.dangdang.com/24060125.html');
INSERT INTO `book` VALUES (277, 'Hadoop生态', ' Hadoop+Spark生态系统操作与实战指南', '余辉', '清华大学出版社', '通过实际操作，快速学习Hadoop Spark大数据技术。解析Hadoop Spark常用组件原理和实战操作，让读者快速了解组件原理和应用。结合Hadoop Spark原生态组件操作，使得读者了解当今互联网应用极为广泛的集群技术。帮助读者构建Hadoop Spark两大生态系统全局观。', 'http://img3m1.ddimg.cn/10/22/1900767241-1_b_1.jpg', 'http://product.dangdang.com/1900767241.html');
INSERT INTO `book` VALUES (278, 'Hadoop生态', ' Spark与Hadoop大数据分析', '（美）文卡特・安卡姆', '机械工业出版社', NULL, 'http://img3m4.ddimg.cn/0/6/25107984-1_b_2.jpg', 'http://product.dangdang.com/25107984.html');
INSERT INTO `book` VALUES (279, 'Hadoop生态', ' Elasticsearch集成Hadoop最佳实践', '[著] (美)Vishal Shukla ，[译]贾传青', '清华大学出版社', 'Hadoop已经是业界公认的大数据处理的事实标准，对海量数据的存储与处理都是不在话下，不过，随着业务的复杂化以及需求的多变，分析师们迫切地想要看到结果从而进行分析，而目前Hadoop离线 批量处理的方式稍微有点力不从心，有没有一种能够更灵活地使用和加工数据的方法呢？鉴于此，Elasticsearch脱颖而出，Elasticsearch可以很方便地对海量数据进行搜索与聚合，并且能够实时响应，用户无需为每次不同的查询而重新定义Schema或者预处理。 数据科学家可以非常灵活地对数据进行各种维度的钻取与分析，把更多的精力关注在业务本身和挖掘数据本身的价值，并且可以随时根据上一个查询得到的线索随时去构建新的查询从而继续挖掘，这在以前是不敢想象的。另外，Elasticsearch还能和Hadoop进行结合，Hadoop 适合海量数据的归档与离线预处理，Elasticsearch则进行实', 'http://img3m0.ddimg.cn/62/19/25089830-1_b_3.jpg', 'http://product.dangdang.com/25089830.html');
INSERT INTO `book` VALUES (280, 'Python', ' Python编程快速上手 让繁琐工作自动化（Python3编程从入门到实践 新手学习必备用书）', '[美] Al Sweigart 斯维加特', '人民邮电出版社', '亚马逊畅销Python编程图书，帮助你通过编程快速实现工作自动化在本书中，你将学习利用Python 编程，在几分钟内完成手工需要几小时的工作，而事先却无需具备编程经验。一旦掌握了编程的基础知识，你就可以毫不费力地创建Python 程序，完成高效的自动化工作，包括', 'http://img3m2.ddimg.cn/1/5/23997502-1_b_6.jpg', 'http://product.dangdang.com/23997502.html');
INSERT INTO `book` VALUES (281, 'Python', ' Python学习手册（原书第5版）', '[美]马克・卢茨', '机械工业出版社', '本书根据Python专家Mark Lutz的著名培训课程编写而成，是易于掌握和自学的Python语言教程。书中以目前主流的Python 3.X为主，同时兼顾Python 2.X的内容，全面、系统讲解Python语言核心知识，每个知识都会以知识点、思想、示例代码的方式详细展开，由浅入深，循序渐进。同时每章都配有章后习题、编程练习及详尽的解答，并且还配有大量注释、示例和图表，便于你学习新的技能和巩固加深自己的理解。无论你从事哪个领域，本书都为你提供了未来全部Python工作的必备知识。', 'http://img3m8.ddimg.cn/27/32/25576578-1_b_5.jpg', 'http://product.dangdang.com/25576578.html');
INSERT INTO `book` VALUES (282, 'Python', ' Python基础教程（第3版）', '[挪]芒努斯・利・海特兰德（Magnus Lie Hetland）', '人民邮电出版社', '本书是经典的Python入门教程，层次鲜明、结构严谨、内容翔实，特别是后面几章，作者将前面讲述的内容应用到10个引人入胜的项目中，并以模板的形式介绍了项目的开发过程，手把手教授Python编程实践，让读者从项目中领略Python的真正魅力。 本书既适合初学者夯实基础，又能帮助Python程序员提升技能，即使是中高-级Python程序员，也能从书里找到耳目一新的内容。', 'http://img3m5.ddimg.cn/62/19/25218035-1_b_2.jpg', 'http://product.dangdang.com/25218035.html');
INSERT INTO `book` VALUES (283, 'Python', ' Python从菜鸟到高手', '李宁', '清华大学出版社', 'JetBrains官方推荐图书！JetBrains官大中华区市场部经理赵磊作序！送2400分钟同步视频课程！500个案例，400道Python练习题，电子书，10万行源代码，6个实战项目！ 李宁 私房菜谱 ', 'http://img3m4.ddimg.cn/6/6/25333314-1_b_2.jpg', 'http://product.dangdang.com/25333314.html');
