INSERT INTO `book` VALUES (385, ' Rails', ' Ruby on Rails实践之路', '（印）沃哈？？著', '科学出版社', '本书为“件研发精品译丛”中的一本。全书覆盖了使用Ruby on Rails开发Web应用的各项技术，包括Ajax，目录服务以及Web服务。书中将Ruby on Rails与主流编程语言Java和开发Web应用*普遍的脚本语言PHP进行了比较。书中提供的示例应用程序修改后可以直接用于读者实际的项目开发活动中。', 'http://img3m2.ddimg.cn/85/9/20830972-1_b_0.jpg', 'http://product.dangdang.com/20830972.html');
