INSERT INTO `book` VALUES (396, 'KVM', ' 深度实践KVM', '肖力， 汪爱伟， 杨俊俊， 赵德禄', '机械工业出版社', '运维畅销书，金山、安居客、盛大、携程运维专家多年大规模生产环境实践经验总结，国内数十位运维专家鼎力支持和推荐； 从核心技术、管理运维、性能优化、项目实施等多个维度系统、深入讲解KVM虚拟化技术的工作原理、应用方法和技术生态，并针对生产环境中的各种难题给出了解决方案', 'http://img3m5.ddimg.cn/65/27/23768975-1_b_1.jpg', 'http://product.dangdang.com/23768975.html');
INSERT INTO `book` VALUES (397, 'KVM', ' KVM虚拟化技术', '任永杰,单海涛', '机械工业出版社', '首本Linux KVM虚拟化技术专著，由Intel虚拟化技术部门资深虚拟化技术专家和KVM研究者撰写 系统介绍KVM虚拟机的功能、特性和使用方法，深入剖析KVM虚拟机的核心技术和原理', 'http://img3m6.ddimg.cn/36/7/1900396116-1_b_4.jpg', 'http://product.dangdang.com/1900396116.html');
INSERT INTO `book` VALUES (398, 'KVM', ' Linux KVM虚拟化架构实战指南', '何坤源', '人民邮电出版社', '・作者技术实力强，长期在金融行业从事IT技术工作，持有RHCE、VCP、ITIL、CCIE等多家IT技术证书。 ・图书内容以实战为主，理论为辅，尽量使用图形化操作，简单容易上手，可迅速提高读者动手能力以及故障处理能力。 ・本书是作者继《VMware vSphere 5.0虚拟化架构实战指南》、《构建高可用VMware vSphere 5.X虚拟化架构》之后的又一部**力作。 ・配套提供www.bdnetlab.com技术网站以及技术群，与读者交流互动', 'http://img3m3.ddimg.cn/31/11/1900702813-1_b_1.jpg', 'http://product.dangdang.com/1900702813.html');
