INSERT INTO `book` VALUES (408, 'Hadoop', ' Hadoop+Spark大数据技术（微课版）', '刘彬斌主编，李柏章、周磊、李永富编著', '清华大学出版社', '包含250分钟微课视频、丰富的实例代码以及真实、完整的项目案例。 内容囊括Linux和MySQL使用基础、Hadoop和Spark集群搭建、Hadoop两大核心的原理与使用、Hadoop生态圈工具（Hive、HBase、Spoop、Flume等）、Scala基础、RDD、Spark SQL、Spark streaming和Spark-ML等知识点，并有配套实例代码。 详细讲解真实的大数据项目 电力能源大数据分析 。', 'http://img3m4.ddimg.cn/85/5/26470804-1_b_1.jpg', 'http://product.dangdang.com/26470804.html');
