INSERT INTO `book` VALUES (598, '万用表', ' 从零开始学万用表检测、应用与维修', '张校珩 主编', '化学工业出版社', '本书针对电工、电子技术人员全面学习和掌握万用表使用技能的需要，内容全面覆盖万用表检测、应用与维修各项基本知识与技能，书中采用图解配视频的形式，零基础的读者也能轻松学会。 内容特点', 'http://img3m1.ddimg.cn/6/5/25582101-1_b_3.jpg', 'http://product.dangdang.com/25582101.html');
