INSERT INTO `book` VALUES (603, '单片机', ' 51单片机C语言程序设计教程', '王云', '人民邮电出版社', '本书从开发的角度，从零开始手把手地带大家学习单片机技术。 基于单片机的*小系统来介绍单片机的基础知识。 以单片机应用开发为主导，循序渐进地讲解单片机常用模块的使用及其编程方法。 通过实践深入理解数字电路的概念、C语言的基本知识以及C语言如何同实际电路发生联系。 讲解单片机应用的扩展知识好编程技巧，单片机项目的开发流程及其方法。 本书配有课件、示例代码和19个教学视频。', 'http://img3m9.ddimg.cn/2/32/25536359-1_b_1.jpg', 'http://product.dangdang.com/25536359.html');
