INSERT INTO `book` VALUES (614, '电气设计', ' EPLAN电气设计实例入门', '张彤，张文涛，张瓒 编著', '北京航空航天大学出版社', '本书是中国介绍EPLAN软件及其设计思想的**本原创图书。EPLAN作为电气计算机辅助设计时代的先锋，一直是为电气规划，工程设计和项目管理领域提供智能化软件解决方案和专业化服务的全球标志型企业。', 'http://img3m0.ddimg.cn/13/10/23537560-1_b_3.jpg', 'http://product.dangdang.com/23537560.html');
