INSERT INTO `book` VALUES (618, '数据库系统', ' 数据库系统概论（第5版）习题解析与实验指导', '王珊 张俊', '高等教育出版社', '本书与《数据库系统概论》（第5版）（高等教育出版社出版）一书配套使用。对配套教材各章基本知识点进行讲解，并对知识点进行了大致分类；给出教材各章习题的解析，同时增加了补充习题及其解答。给出数据库实验环境建设及实验数据准备的技术和方法，详细介绍教材各章实验内容，并对实验中常见的问题给出解答。提供了学习数据库的相关学习资料，包括数据库领域图灵奖获得者介绍及数据库基准测试TPC-C和TPC-H简介。', 'http://img3m1.ddimg.cn/35/25/25205831-1_b_3.jpg', 'http://product.dangdang.com/25205831.html');
INSERT INTO `book` VALUES (619, '数据库系统', ' 数据库系统工程师教程（第3版）', '王亚平', '清华大学出版社', '数据库系统工程师教程（第5版）依据2018年审定通过的数据库系统工程师考试大纲大纲编写，涵盖数据库系统工程师（中级）岗位所要求的主要知识及应用技术。通过数据库系统工程师考试的考生可以获得由人力资源和社会保障部、工业和信息化部认可的职业资格证书，本考试为中级资格认证。?', 'http://img3m3.ddimg.cn/9/4/25247583-1_b_3.jpg', 'http://product.dangdang.com/25247583.html');
INSERT INTO `book` VALUES (620, 'COCOS2D-X', ' 精通Cocos2d-x游戏开发（基础卷）+精通Cocos2d-x游戏开发（进阶卷）（套装共2册）', '王永宝', '清华大学出版社', 'Cocos2d-x创始人王哲先生倾情推荐！游戏开发牛人，四年时间，三易其稿，精心打磨，成就这本精良之作专注于Cocos2d-x引擎底层机制，致力于让初学者成为一个基础扎实、靠谱的程序员快速入门 基础框架 UI与交互 CocoStudio工具链，都是干货，满满诚意Cocos2d-x实用技术 Lua技术 网络游戏开发 跨平台开发内容新颖，以当前流行的Cocos2d-x游戏引擎版本3.x为主进行讲解深入讲解了指针、内存、纹理等大多数初学者掌握不牢固的重要知识点深入剖析了Cocos2d-x引擎在Android、Win32和iOS等系统下的运行流程深入介绍热更新、加密和解密、骨骼动画、调试技巧和物理引擎等实用技术详解在Cocos2d-x中使用Lua的技巧，以及Quick-Cocos2d-x框架和原生Lua框架的区别重点介绍弱联网、强联网和局域网这三种网络游戏的前后端开发详解iOS和Android平台下的开发和打包，涵盖证书签名、ABI、JNI等', 'http://img3m8.ddimg.cn/96/15/25225098-1_b_3.jpg', 'http://product.dangdang.com/25225098.html');
INSERT INTO `book` VALUES (621, 'COCOS2D-X', ' Cocos2d-x之Lua核心编程（第2版）', '刘克男', '清华大学出版社', '作为使用Lua进行Cocos开发的专业图书，本书非常重要！它不但在Lua语言和引擎方面有详细的讲解，更在引擎的诸多细节上花费了大量心思，包括缓存、内存管理与监控、数据加密等，是不可多得的Cocos2d-x开发进阶*教材！ 卞安（网名', 'http://img3m1.ddimg.cn/93/28/25112631-1_b_3.jpg', 'http://product.dangdang.com/25112631.html');
INSERT INTO `book` VALUES (622, 'COCOS2D-X', ' Cocos2d-x 3.x 案例开发大全 第2版', '吴亚峰 苏亚光', '人民邮电出版社', '涵盖了大部分使用Cocos2d-x进行游戏开发时所需的流行技术，如粒子系统、关卡设计、计算几何、2D及3D物理引擎、模型加载、骨骼动画、网络对战、VR虚拟现实等 书中9个综合案例，既包括单机版的游戏项目案例，也有网络对战游戏的项目案例；既包括2D画面呈现的游戏案例，也包括3D画面呈现的游戏案例，以及流行的VR游戏案例 本书附赠的学习资源中包含了书中所有案例的完整源代码，大部分案例都提供了Android的项目版本以及iOS的项目版本，方便读者根据自己的需要选用。', 'http://img3m2.ddimg.cn/12/20/25306392-1_b_2.jpg', 'http://product.dangdang.com/25306392.html');
INSERT INTO `book` VALUES (623, 'COCOS2D-X', ' Cocos2d-x之Lua核心编程（第2版）', '刘克男', '清华大学出版社', NULL, 'http://img3m0.ddimg.cn/78/28/1900767210-1_b_1.jpg', 'http://product.dangdang.com/1900767210.html');
INSERT INTO `book` VALUES (624, 'COCOS2D-X', ' 精通Cocos2d-x游戏开发(进阶卷)', '王永宝,冯志强', '清华大学出版社', 'Cocos2d-x 创始人王哲先生倾情推荐！ Cocos2d-x 实用技术 Lua 技术 网络游戏开发 跨平台开发； 专注于Cocos2d-x 引擎的实际应用，致力于让你成为一个高水平的程序员； 紧跟技术趋势，以当前主流的Cocos2d-x游戏引擎版本3.x进行讲解； 重点介绍实际开发中碰到的各种问题的不同解决思路及其优缺点； 用简洁和通俗易懂的语言写作，读者阅读起来不会枯燥乏味； 深入介绍热更新、加密与解密、骨骼动画、调试技巧、Shader特效及物理引擎等实用技术； 详解在Cocos2d-x中使用Lua的技巧，以及Quick-Cocos2d-x框架和原生Lua框架的区别； 详解网络及实时同步技术，涵盖弱联网、强联网和局域网三种网络游戏的前后端开发； 详解iOS和Android平台下的开发和打包，涵盖证书签名、ABI和JNI等实用技术； 详解如何使用AnySDK在iOS和Android下快速接入第三方SD', 'http://img3m2.ddimg.cn/98/23/1900682882-1_b_1.jpg', 'http://product.dangdang.com/1900682882.html');
INSERT INTO `book` VALUES (625, '图形学', ' 计算机图形学（第四版）', '（美）赫恩 著，蔡士杰，杨若瑜 译', '电子工业出版社', '本书是一本经典著作，这次版本更新增加了许多实践内容，覆盖了近年来计算机图形学的**发展和成就，并附有使用OpenGL编写的大量程序以及各种效果图。本书共分24章，全面系统地讲解了计算机图形学的基本概念和相关技术。作者首先对计算机图形学进行综述；然后讲解了二维图形的对象表示、算法及应用，三维图形的相关技术、建模和变换等；接着介绍了层次建模、动画技术、样条曲线表示、纹理处理等方面的内容，*后光照模型、颜色模型和交互输入法等。', 'http://img3m4.ddimg.cn/59/33/23615024-1_b_0.jpg', 'http://product.dangdang.com/23615024.html');
