INSERT INTO `book` VALUES (633, 'lua', ' Nginx实战', '王力', '电子工业出版社', '1.业内专家刘凯、冯浩、屈耀华、杨明翰、彭赫联袂力荐。 2.本书深入浅出地讲解了Nginx Lua在实战场景中的各种使用技巧和方法，涉及配置、模块、缓存、日志分析、静态容灾、反向代理、爬虫、性能分析与优化等众多方面，帮助提升服务性能。 3.所有实战案例都有配套源码，方便读者学习和使用。', 'http://img3m8.ddimg.cn/43/27/26511748-1_b_3.jpg', 'http://product.dangdang.com/26511748.html');
