INSERT INTO `book` VALUES (732, '美工设计', ' 零基础学Photoshop淘宝美工设计 全视频教学版', '麓山文化', '人民邮电出版社', '本书根据作者多年教学经验和实战经验编写而成，以基础知识与练习实训相结合的形式，详细讲解了图像处理软件Photoshop 淘宝美工设计的应用技巧。 随书提供资源文件，包含本书的实训素材文件、案例文件和视频文件，读者在学习的过程中可以随时进行调用。同时赠送多种淘宝店铺装修常用素材模板，包括标签模板、海报模板、淘宝主页、文字模板、主图模板', 'http://img3m1.ddimg.cn/39/26/26483331-1_b_1.jpg', 'http://product.dangdang.com/26483331.html');
