INSERT INTO `book` VALUES (738, ' web框架', ' Web前端开发 使用ASP.NET Core、Angular和Bootstrap', '[美]西蒙尼？基西雷塔 (Simone Chiaretta) 杜静 敖富江 李博 译', '清华大学出版社', '? 让读者了解所有新的和改进的ASP.NET Core功能 ? 开发用户期望的功能丰富、灵活且响应敏捷的设计 ? 描述使用ASP.NET Core进行前端开发的*、*实践 ? 详述如何大限度地利用具有非原生功能的第三方库 ? 解释如何组合应用ASP.NET Core与不同的工具、框架和库 ? 探讨Bower依赖关系、gulp构建系统与ASP.NET Core的使用', 'http://img3m6.ddimg.cn/57/6/26439096-1_b_3.jpg', 'http://product.dangdang.com/26439096.html');
INSERT INTO `book` VALUES (739, ' web框架', ' 移动Web应用开发教程――HTML5+JavaScript框架全栈App开发', '周化钢', '清华大学出版社', '《移动Web应用开发教程 HTML5 JavaScript框架全栈App开发》系统地讲解移动web应用开发的*技术，从简单的B/S应用原理开始，到*的HTML5技术规范，从各种复杂的移动开发框架，精选一两个流行的框架做为入门，通俗易懂。', 'http://img3m1.ddimg.cn/39/23/25335921-1_b_2.jpg', 'http://product.dangdang.com/25335921.html');
INSERT INTO `book` VALUES (740, '后台程序', ' PHP7实践指南', '陈小龙', '清华大学出版社', '在Web发领域，PHP因免费源、语法简单属于类C风格语言，具有良好的跨平台性而受到广大业内人士的支持。经过多个预发布版本， PHP 5.0在2004年7月13日发布。该版本使用Zend引擎Ⅱ，并且加了新功能，完全支持面向对象。2015年12月3日，PHP 7.0.0 GA发布，性能较PHP 5.6提升了两倍，新增了一些操作符和函数的返回类型声明，也增加了对匿名类的支持等。关于PHP 7的讨论在网上也逐渐展。不过到目前为止，国内有关专门介绍PHP 7应用发的书籍还很少，本书的目的就是对现有的PHP 7技术行一个汇总，书中内容是笔者在PHP 7学习和实际工作项目中的心得体会和系统总结，希望能够帮助PHP 7学习者更好地了解其新特性，并应用于实际发中。', 'http://img3m1.ddimg.cn/89/22/1900736531-1_b_1.jpg', 'http://product.dangdang.com/1900736531.html');
INSERT INTO `book` VALUES (741, '后台程序', ' PHP7实践指南', '陈小龙', '清华大学出版社', '在Web开发领域，PHP因免费开源、语法简单属于类C风格语言，具有良好的跨平台性而受到广大业内人士的支持。经过多个预发布版本， PHP 5.0在2004年7月13日发布。该版本使用Zend引擎Ⅱ，并且加入了新功能，完全支持面向对象。2015年12月3日，PHP 7.0.0 GA发布，性能较PHP 5.6提升了两倍，新增了一些操作符和函数的返回类型声明，也增加了对匿名类的支持等。关于PHP 7的讨论在网上也逐渐展开。不过到目前为止，国内有关专门介绍PHP 7应用开发的书籍还很少，本书的目的就是对现有的PHP 7技术进行一个汇总，书中内容是笔者在PHP 7学习和实际工作项目中的心得体会和系统总结，希望能够帮助PHP 7学习者更好地了解其新特性，并应用于实际开发中。', 'http://img3m8.ddimg.cn/60/17/25089828-1_b_3.jpg', 'http://product.dangdang.com/25089828.html');
