INSERT INTO `book` VALUES (747, '数据库安全', ' 数据中心设计与管理', '林予松、李润知、刘炜', '清华大学出版社', '1、云计算和虚拟化基础知识，数据中心的发展历程、主要特点及未来的发展趋势。2、数据中心建设中有关基础环境方面的相关技术和标准，包括数据中心的选址、功能区划分、布线系统、供配电系统、空调系统、防护系统、监控系统等。3、数据中心的网络子系统，包括数据中心网络的规划与设计、数据中心主要网络设备的工作原理、市场上针对数据中心的主流网络产品、数据中心网络的新技术以及发展趋势。数据中心的计算子系统，包括两种不同计算架构的主要特点、服务器相关技术以及计算虚拟化技术和产品。4、数据中心的存储子系统，包括存储基本技术和RAID、存储虚拟化相关技术、数据备份和容灾技术。5、数据中心的安全子系统，包括信息安全概述、云数据中心面临的主要安全威胁、在数据中心部署的主要安全产品，并通过实际案例介绍如何构建数据中心', 'http://img3m5.ddimg.cn/8/26/25159175-1_b_3.jpg', 'http://product.dangdang.com/25159175.html');
