INSERT INTO `book` VALUES (793, '防火墙', ' 防火墙产品原理与应用', '张艳,等', '电子工业出版社', '本书共5章，从防火墙产品的技术实现和标准介绍手，对其产生需求、发展历程、实现原理、技术标准、应用场景和典型产品等内容行了全面翔实的介绍。本书系信息安全产品技术丛书之一。作者系业内相关标准制定者，具有一定的权威性。', 'http://img3m5.ddimg.cn/40/1/1900777765-1_b_1.jpg', 'http://product.dangdang.com/1900777765.html');
