INSERT INTO `book` VALUES (840, '爬虫', ' 自己动手写网络爬虫(修订版)', '罗刚', '清华大学出版社', '大多数网络爬虫的开发原理与技巧在专业的公司内部都秘而不宣，至今仍然缺少理论与实践相结合的专门介绍网络爬虫的书籍。本书尝试理论与实践相结合，深入透彻地讲解网络爬虫的原理并且辅以相关代码作为参考。', 'http://img3m3.ddimg.cn/61/20/24028153-1_b_6.jpg', 'http://product.dangdang.com/24028153.html');
