INSERT INTO `book` VALUES (932, '工程项目', ' 工程项目管理', '傅鸣春,侯献语,黄超', '华中科技大学出版社', '本书按照高等职业教育土建类各专业对工程项目管理课程的要求，根据项目管理类专业的标准，以国家现行建设工程标准、规范、规程为依据，参照现行国家注册建造师 建设工程项目管理 科目内容和考试大纲，根据编者多年工作经验和教学实践，在结合国内建筑工程技术管理的发展，以及同事们已有成果的基础上修改、补充编写而成。本书对建筑工程项目管理的理论、方法、要求等做了明确的阐述，坚持以就业为导向，突出实用性、实践性。', 'http://img3m2.ddimg.cn/15/10/27851982-1_b_2.jpg', 'http://product.dangdang.com/27851982.html');
